protected void grdHistory_Filtering(object sender, GridFilteringEventArgs e)\n{\n    if (e.Column.DataField == "DeviceHistoryID")\n    {\n        string filterValue = e.FilterValue;\n        foreach (GridRow row in grdHistory.Rows)\n        {\n            Hashtable records = (Hashtable)row.DataItem;\n            string eventDescription = FormatEventDescription(records);\n            if (eventDescription.Contains(filterValue))\n            {\n                row.Visible = true;\n            }\n            else\n            {\n                row.Visible = false;\n            }\n        }\n        e.Cancel = true; // cancel the default filtering behavior\n    }\n}