private static IEnumerable<Range<int>> GenerateRanges()\n{\n    var r = new Random();\n    var n = 10000;\n    while (--n >= 0)\n    {\n        var start = r.Next(10000);\n        var length = (int)Math.Round(r.NextGaussian(10, 20)); // Normal distribution with mean 10 and stddev 20\n        var end = start + length;\n        yield return Range.Create(start, end);\n    }\n}\n\npublic static double NextGaussian(this Random r, double mean, double stddev)\n{\n    var u1 = r.NextDouble();\n    var u2 = r.NextDouble();\n    var normal = Math.Sqrt(-2.0 * Math.Log(u1)) * Math.Sin(2.0 * Math.PI * u2);\n    return mean + stddev * normal;\n}