string verification_url = @"http://site.com/posttest.php";\nstring verification_data = "test=524001A";\nstring result = string.Empty;\nresult = Post(verification_url, verification_data);\n\npublic string Post(string url, string data)\n{\n    string result = "";\n    try\n    {\n        string encodedData = "test=" + HttpUtility.UrlEncode("524001A");\n        byte[] buffer = Encoding.ASCII.GetBytes(encodedData);\n        HttpWebRequest WebReq = (HttpWebRequest)WebRequest.Create(@url);\n        WebReq.Method = "POST";\n\n        WebReq.ContentType = "application/x-www-form-urlencoded";             \n        WebReq.ContentLength = buffer.Length;\n        Stream PostData = WebReq.GetRequestStream();\n\n        PostData.Write(buffer, 0, buffer.Length);\n        PostData.Close();\n\n        HttpWebResponse WebResp = (HttpWebResponse)WebReq.GetResponse();\n        Stream Answer = WebResp.GetResponseStream();\n        StreamReader _Answer = new StreamReader(Answer);\n        result = _Answer.ReadToEnd();\n    }\n    catch (Exception ex)\n    {\n        MessageBox.Show(ex.Message);\n    }\n    if (result.Length < 0)\n    result = "";\n\n    return result;\n}