// Create a DataTable to hold the list of words\nDataTable dt = new DataTable();\ndt.Columns.Add("word", typeof(string));\n\n// Add the words to the DataTable\nforeach (string word in listOfWords)\n{\n    dt.Rows.Add(word);\n}\n\n// Create a SqlParameter with the UDTT type\nSqlParameter param = new SqlParameter("@LISTOFWORDS", SqlDbType.Structured);\nparam.TypeName = "StringList";\nparam.Value = dt;\n\n// Execute the stored procedure\nusing (SqlConnection conn = new SqlConnection(connectionString))\n{\n    conn.Open();\n    SqlCommand cmd = new SqlCommand("MyProc", conn);\n    cmd.CommandType = CommandType.StoredProcedure;\n    cmd.Parameters.Add(param);\n    cmd.ExecuteNonQuery();\n}