public partial class ProductosTableAdapter\n{\n    public void UpdateWithJoin(dTiendasDs.ProductosDataTable dataTable)\n    {\n        // Create a new SqlCommandBuilder to generate the update command\n        SqlCommandBuilder commandBuilder = new SqlCommandBuilder(this.Adapter);\n\n        // Get the update command\n        SqlCommand updateCommand = commandBuilder.GetUpdateCommand();\n\n        // Modify the update command to include the join\n        updateCommand.CommandText = "UPDATE Productos SET Price = @Price FROM Productos INNER JOIN Prices ON Productos.IdProduct = Prices.IdProduct WHERE Productos.IdProduct = @IdProduct";\n\n        // Add the parameters\n        updateCommand.Parameters.AddWithValue("@Price", dataTable.Rows[0]["Price"]);\n        updateCommand.Parameters.AddWithValue("@IdProduct", dataTable.Rows[0]["IdProduct"]);\n\n        // Execute the update command\n        this.Adapter.UpdateCommand = updateCommand;\n        this.Adapter.Update(dataTable);\n    }\n}