protected void Repeater1_ItemCommand(object source, RepeaterCommandEventArgs e)\n{\n    if (e.CommandName == "Update")\n    {\n        int questionNumber = Convert.ToInt32(e.CommandArgument);\n        TextBox txtAnswer = (TextBox)e.Item.FindControl("txtAnswer");\n        string answer = txtAnswer.Text;\n\n        ObjectDataSource1.UpdateParameters["questionNumber"].DefaultValue = questionNumber.ToString();\n        ObjectDataSource1.UpdateParameters["answer"].DefaultValue = answer;\n\n        ObjectDataSource1.Update();\n    }\n}