using Lucene.Net.Index;\nusing Lucene.Net.Store;\nusing Lucene.Net.Analysis.Standard;\nusing Lucene.Net.Documents;\n\n// Create a Lucene directory\nvar directory = new MMapDirectory(new DirectoryInfo("index"));\n\n// Create an analyzer\nvar analyzer = new StandardAnalyzer(Lucene.Net.Util.Version.LUCENE_30);\n\n// Create an index writer\nvar writer = new IndexWriter(directory, analyzer, IndexWriter.MaxFieldLength.UNLIMITED);\n\n// Create a document\nvar document = new Document();\ndocument.Add(new Field("content", "This is the content of the PDF file.", Field.Store.YES, Field.Index.ANALYZED));\ndocument.Add(new Field("filename", "example.pdf", Field.Store.YES, Field.Index.NOT_ANALYZED));\n\n// Add the document to the index\nwriter.AddDocument(document);\n\n// Close the writer\nwriter.Close();