using System.Data;\nusing System.Data.OleDb;\n\npublic DataSet ReadAccessFile(string filePath)\n{\n    DataSet dataSet = new DataSet();\n\n    using (OleDbConnection connection = new OleDbConnection("Provider=Microsoft.Jet.OLEDB.4.0;Data Source=" + filePath))\n    {\n        connection.Open();\n\n        DataTable schemaTable = connection.GetSchema("Tables");\n        foreach (DataRow row in schemaTable.Rows)\n        {\n            string tableName = row["TABLE_NAME"].ToString();\n            if (tableName.StartsWith("MSys")) continue; // skip system tables\n\n            OleDbDataAdapter adapter = new OleDbDataAdapter("SELECT * FROM " + tableName, connection);\n            adapter.Fill(dataSet, tableName);\n        }\n    }\n\n    return dataSet;\n}