using System;\nusing System.Diagnostics;\n\nclass MyModule\n{\n    public void MyMethod()\n    {\n        // Code to profile\n    }\n}\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        var profiler = new System.Diagnostics.PerformanceCounter("MyModule", "MyMethod");\n        profiler.Start();\n\n        var myModule = new MyModule();\n        myModule.MyMethod();\n\n        profiler.Stop();\n    }\n}