using System;\nusing System.Runtime.InteropServices;\n\npublic class MessageBoxHelper\n{\n    [DllImport("user32.dll", CharSet = CharSet.Unicode)]\n    static extern IntPtr FindWindow(IntPtr hWndParent, string lpszClass, string lpszWindow);\n\n    [DllImport("user32.dll", CharSet = CharSet.Unicode)]\n    static extern IntPtr FindWindowEx(IntPtr hWndParent, IntPtr hWndChildAfter, string lpszClass, string lpszWindow);\n\n    [DllImport("user32.dll")]\n    static extern IntPtr SendMessage(IntPtr hWnd, uint Msg, IntPtr wParam, IntPtr lParam);\n\n    public static void ClickMessageBoxButton(string caption, string buttonCaption)\n    {\n        // Find the message box\n        IntPtr hWnd = FindWindow(IntPtr.Zero, caption);\n\n        // Find the button\n        IntPtr buttonHandle = FindWindowEx(hWnd, IntPtr.Zero, "Button", buttonCaption);\n\n        // Simulate a click on the button\n        SendMessage(buttonHandle, 0x0201 /*WM_LBUTTONDOWN*/, IntPtr.Zero, IntPtr.Zero);\n        SendMessage(buttonHandle, 0x0202 /*WM_LBUTTONUP*/, IntPtr.Zero, IntPtr.Zero);\n    }\n}