var ranges = new[] {\n    new Range<int> { Start = 1, End = 5 },\n    new Range<int> { Start = 3, End = 9 },\n    new Range<int> { Start = 11, End = 15 },\n    new Range<int> { Start = 12, End = 14 },\n    new Range<int> { Start = 13, End = 20 },\n};\n\nvar collapsedRanges = ranges.Collapse(Comparer<int>.Default);\n\nforeach (var range in collapsedRanges)\n{\n    Console.WriteLine($"Start: {range.Start}, End: {range.End}");\n}