public static class PopularityCalculator\n{\n    public static double CalculatePopularity(int score, DateTime creationDate)\n    {\n        const double gravity = 1.8;\n        const double timePeriod = 45000; // 45 days\n\n        var age = (DateTime.UtcNow - creationDate).TotalSeconds;\n        var weightedAge = Math.Pow(age, gravity);\n        var weightedScore = score / Math.Pow(timePeriod, gravity);\n\n        return weightedScore / (weightedAge + 2);\n    }\n}