using System;\nusing System.IO;\nusing System.Text.RegularExpressions;\n\npublic class TnsnamesParser\n{\n    public static string[] ParseTnsnames(string filePath)\n    {\n        string[] tnsnames = new string[0];\n        string content = File.ReadAllText(filePath);\n        string pattern = @"^([A-Za-z0-9_]+)\s*=\s*\(DESCRIPTION";\n\n        MatchCollection matches = Regex.Matches(content, pattern, RegexOptions.Multiline);\n        tnsnames = new string[matches.Count];\n        for (int i = 0; i < matches.Count; i++)\n        {\n            tnsnames[i] = matches[i].Groups[1].Value;\n        }\n        return tnsnames;\n    }\n}