using System.Text.RegularExpressions;\n\npublic static string CleanRtf(string rtfText)\n{\n    // Define the formatting tags you want to keep\n    var allowedTags = new[] { @"\ul", @"\b", @"\i" };\n\n    // Remove all RTF tags except the allowed ones\n    var cleanedText = Regex.Replace(rtfText, @"\\(?!(" + string.Join("|", allowedTags) + "))\w+", "");\n\n    // Remove all RTF groups (e.g. {\fonttbl...})\n    cleanedText = Regex.Replace(cleanedText, @"{[^}]*}", "");\n\n    // Remove all RTF escape sequences (e.g. \highlight1)\n    cleanedText = Regex.Replace(cleanedText, @"\\[a-zA-Z]+\d*", "");\n\n    return cleanedText;\n}