// Define a custom attribute\n[AttributeUsage(AttributeTargets.Assembly)]\npublic class WatermarkAttribute : Attribute\n{\n    public string Value { get; set; }\n}\n\n// Apply the attribute to the assembly\n[assembly: Watermark("YourUniqueWatermarkValue")]\n\nclass Program\n{\n    static void Main()\n    {\n        var assembly = Assembly.GetExecutingAssembly();\n        var watermarkAttribute = (WatermarkAttribute)assembly.GetCustomAttributes(typeof(WatermarkAttribute), false).FirstOrDefault();\n        var watermark = watermarkAttribute?.Value;\n        // Use the watermark value as needed\n    }\n}