using StringTemplate;\n\npublic class AlgorithmGenerator\n{\n  public static string GenerateCSharpCode(int inputParameter1, int inputParameter2)\n  {\n    var template = new StringTemplate("$if(inputParameter1 == 1)$ return \"one\" $elseif(inputParameter2 == 5)$ return \"five\" + inputParameter1 $else$ return \"not found\" $endif$");\n    template.SetAttribute("inputParameter1", inputParameter1);\n    template.SetAttribute("inputParameter2", inputParameter2);\n    return template.ToString();\n  }\n\n  public static string GenerateJavaCode(int inputParameter1, int inputParameter2)\n  {\n    var template = new StringTemplate("$if(inputParameter1 == 1)$ return \"one\"; $elseif(inputParameter2 == 5)$ return \"five\" + inputParameter1; $else$ return \"not found\"; $endif$");\n    template.SetAttribute("inputParameter1", inputParameter1);\n    template.SetAttribute("inputParameter2", inputParameter2);\n    return template.ToString();\n  }\n}