using System;\nusing System.IO;\nusing System.Net;\nusing System.Web.Services;\n\n[WebService(Namespace = "https://your-namespace.com/")]\npublic class WeatherBugProxy : WebService\n{\n    [WebMethod]\n    public string GetWeather(string apiCode, string query)\n    {\n        string url = $"http://{apiCode}.api.wxbug.net/weatherservice.asmx?{query}";\n        HttpWebRequest request = (HttpWebRequest)WebRequest.Create(url);\n        request.Method = "GET";\n        HttpWebResponse response = (HttpWebResponse)request.GetResponse();\n        string responseBody = new StreamReader(response.GetResponseStream()).ReadToEnd();\n        return responseBody;\n    }\n}