using MsgReader;\n\n// Load the .msg file\nusing (var msgFile = new MsgFile("path_to_your_msg_file.msg"))\n{\n    // Extract the required fields\n    var from = msgFile.GetPropertyValue(MsgProperty.PR_SENDER_NAME_W);\n    var to = msgFile.GetPropertyValue(MsgProperty.PR_DISPLAY_TO_W);\n    var cc = msgFile.GetPropertyValue(MsgProperty.PR_DISPLAY_CC_W);\n    var bcc = msgFile.GetPropertyValue(MsgProperty.PR_DISPLAY_BCC_W);\n    var sentDate = msgFile.GetPropertyValue(MsgProperty.PR_CLIENT_SUBMIT_TIME);\n    var receiveDate = msgFile.GetPropertyValue(MsgProperty.PR_MESSAGE_DELIVERY_TIME);\n\n    Console.WriteLine("From: " + from);\n    Console.WriteLine("To: " + to);\n    Console.WriteLine("CC: " + cc);\n    Console.WriteLine("BCC: " + bcc);\n    Console.WriteLine("Sent Date: " + sentDate);\n    Console.WriteLine("Receive Date: " + receiveDate);\n}