using Microsoft.SharePoint;\nusing Microsoft.SharePoint.Security;\n\nnamespace CustomEventReceiver\n{\n    public class FormReceiver : SPItemEventReceiver\n    {\n        public override void ItemAdded(SPItemEventProperties properties)\n        {\n            base.ItemAdded(properties);\n\n            // Get the current web and lists\n            SPWeb web = properties.Web;\n            SPList publicList = web.Lists["form2"];\n            SPList privateList = web.Lists["form1"];\n\n            // Move the submitted form to the private list\n            SPListItem item = properties.ListItem;\n            item.MoveTo(privateList);\n\n            // Update the item to set the correct permissions\n            item.UpdateOverwriteVersion();\n        }\n    }\n}