public class MyDbContext : DbContext\n{\n    private readonly string _prefix;\n\n    public MyDbContext(DbContextOptions<MyDbContext> options, string prefix)\n        : base(options)\n    {\n        _prefix = prefix;\n    }\n\n    protected override void OnModelCreating(ModelBuilder modelBuilder)\n    {\n        modelBuilder.Model.GetEntityTypes()\n            .ToList()\n            .ForEach(entityType =>\n            {\n                entityType.Relational().TableName = $"{_prefix}{entityType.Relational().TableName}";\n            });\n    }\n}