// Using a custom attribute to store the long name\n[LongName("DescriptiveClassName")]\npublic class ShortName\n{\n    // Using a custom attribute to store the long name\n    [LongName("DescriptiveMethodName")]\n    public void ShortMethod()\n    {\n        // Code here\n    }\n}\n\n// Example usage of the addin\npublic class AddinExample\n{\n    public void UseShortNames()\n    {\n        ShortName shortName = new ShortName();\n        shortName.ShortMethod();\n    }\n\n    public void UseLongNames()\n    {\n        // The addin could replace the short names with the long names\n        DescriptiveClassName descriptiveClass = new DescriptiveClassName();\n        descriptiveClass.DescriptiveMethodName();\n    }\n}