public class MyController : Controller\n{\n    [HttpPost]\n    public ActionResult StartJob()\n    {\n        // Kick off the long-running job\n        JobStatus status = StartLongRunningJob();\n        return Json(status);\n    }\n\n    [HttpGet]\n    public ActionResult GetJobStatus()\n    {\n        // Return the current status of the job\n        JobStatus status = GetJobStatusFromDatabaseOrCache();\n        return Json(status);\n    }\n}\n\npublic class JobStatus\n{\n    public string Status { get; set; } // "working" or "done"\n}