using System.Security.Cryptography;\nusing System.Text;\n\nclass CredentialStore\n{\n    public static void StoreCredentials(string username, string password)\n    {\n        byte[] data = Encoding.UTF8.GetBytes($"{username}:{password}");\n        byte[] entropy = new byte[16];\n        using (var rng = new RNGCryptoServiceProvider())\n        {\n            rng.GetBytes(entropy);\n        }\n        ProtectedData.Protect(data, entropy, DataProtectionScope.CurrentUser);\n        // Store the protected data in a secure location, such as a file or registry key\n    }\n\n    public static (string, string) RetrieveCredentials()\n    {\n        byte[] protectedData = // Read the protected data from the secure location\n        byte[] entropy = new byte[16];\n        using (var rng = new RNGCryptoServiceProvider())\n        {\n            rng.GetBytes(entropy);\n        }\n        byte[] data = ProtectedData.Unprotect(protectedData, entropy, DataProtectionScope.CurrentUser);\n        string[] credentials = Encoding.UTF8.GetString(data).Split(':');\n        return (credentials[0], credentials[1]);\n    }\n}