HttpWebRequest request = (HttpWebRequest)WebRequest.Create("https://test.com/webservice?wsdl");\nrequest.Proxy = new WebProxy("1.2.3.4", 8080);\nrequest.Proxy.Credentials = new NetworkCredential("username", "password");\n\nrequest.Headers["Authorization"] = "Basic " + Convert.ToBase64String(Encoding.ASCII.GetBytes("username:password"));\n\nHttpWebResponse response = (HttpWebResponse)request.GetResponse();\n\nstring responseData = new StreamReader(response.GetResponseStream()).ReadToEnd();