using System.Xml;\nusing System.Xml.XPath;\n\n// Load the XML document\nXmlDocument xmlDoc = new XmlDocument();\nxmlDoc.Load("example.xml");\n\n// Create an XPathNavigator to select the nodes to modify\nXPathNavigator nav = xmlDoc.CreateNavigator();\nXPathNodeIterator iterator = nav.Select("//node/to/modify");\n\n// Iterate over the selected nodes and modify their InnerXml\nwhile (iterator.MoveNext())\n{\n    XmlNode node = ((IHasXmlNode)iterator.Current).GetNode();\n    node.InnerXml = "new inner xml";\n}\n\n// Save the modified XmlDocument to the file\nxmlDoc.Save("example.xml");