public void UpdateMyTable(int primarykey, string column1, string column2)\n{\n  using (SqlConnection connection = new SqlConnection(connectionString))\n  {\n    connection.Open();\n    SqlCommand command = new SqlCommand("UPDATE myTable SET column1 = @column1, column2 = @column2 WHERE actualPrimaryKey = @actualPrimaryKey", connection);\n    command.Parameters.AddWithValue("@column1", column1);\n    command.Parameters.AddWithValue("@column2", column2);\n    command.Parameters.AddWithValue("@actualPrimaryKey", primarykey);\n    command.ExecuteNonQuery();\n  }\n}