using System.IO;\n\npublic static void GetFiles(string path)\n{\n    try\n    {\n        foreach (string file in Directory.EnumerateFiles(path, "*", SearchOption.AllDirectories))\n        {\n            // Process the file\n            Console.WriteLine(file);\n        }\n    }\n    catch (UnauthorizedAccessException ex) when (ex.Message.Contains("System Volume Information"))\n    {\n        // Ignore system volume information directory\n    }\n    catch (UnauthorizedAccessException ex)\n    {\n        // Handle other unauthorized access exceptions\n        Console.WriteLine($"Error accessing {ex.Message}");\n    }\n}\n\n// To scan a non-system drive fully\npublic static void ScanDrive(string driveLetter)\n{\n    string drivePath = $"{driveLetter}:\\";\n    GetFiles(drivePath);\n}