using System;\nusing System.Web.Services.Protocols;\nusing Microsoft.ReportingServices.ReportingService;\n\nclass SubscriptionManager\n{\n    private ReportingService _rs;\n\n    public SubscriptionManager(string reportServerUrl)\n    {\n        _rs = new ReportingService(reportServerUrl);\n    }\n\n    public void CreateSubscription(string reportName, string subscriptionName, string frequency)\n    {\n        // Create a new subscription\n        Subscription subscription = new Subscription();\n        subscription.ReportName = reportName;\n        subscription.SubscriptionName = subscriptionName;\n        subscription.Frequency = frequency; // e.g. "Daily", "Weekly", etc.\n\n        // Set up the subscription parameters\n        ParameterValue[] parameters = new ParameterValue[1];\n        parameters[0] = new ParameterValue();\n        parameters[0].Name = "ReportParameter1";\n        parameters[0].Value = "ParameterValue1";\n\n        // Create the subscription\n        _rs.CreateSubscription(subscription, parameters);\n    }\n}