using System.Diagnostics;\n\nclass CygwinRubyExecutor\n{\n    public static void Execute(string rubyScriptPath)\n    {\n        var cygwinBatchPath = @"C:\cygwin\cygwin.bat";\n        var rubyExePath = @"C:\cygwin\bin\ruby.exe";\n\n        var processInfo = new ProcessStartInfo\n        {\n            FileName = cygwinBatchPath,\n            Arguments = $"-c \"{rubyExePath} {rubyScriptPath}\"",\n            RedirectStandardError = true,\n            RedirectStandardOutput = true,\n            UseShellExecute = false,\n            CreateNoWindow = true\n        };\n\n        using (var process = Process.Start(processInfo))\n        {\n            process.WaitForExit();\n        }\n    }\n}