var result = from p in Persons\n             join pic in PersonInContact on p.PersonID equals pic.PersonID\n             join c in Contacts on pic.ContactID equals c.ContactID\n             group c by p.PersonID into g\n             select new \n             {\n                 PersonID = g.Key,\n                 BusinessContact = g.Where(c => c.ContactType == 1).FirstOrDefault(),\n                 HomeContact = g.Where(c => c.ContactType == 2).FirstOrDefault()\n             };