// Controller\npublic class MyController : Controller\n{\n    public ActionResult Index()\n    {\n        return View();\n    }\n\n    public JsonResult GetData()\n    {\n        // Return some data in JSON format\n        return Json(new { name = "John Doe", age = 30 });\n    }\n}\n\n// View\n@model MyModel\n@{\n    ViewBag.Title = "My Page";\n}\n\n// Use jQuery to call the GetData action\n$.ajax({\n    type: 'GET',\n    url: '@Url.Action("GetData", "MyController")',\n    dataType: 'json',\n    success: function(data) {\n        console.log(data);\n    }\n});