using System.Data.SqlClient;\n\n// Create a SqlConnection object\nSqlConnection connection = new SqlConnection();\n\n// Set the connection string without specifying a database\nconnection.ConnectionString = "Server=myServerAddress;User Id=MACHINE\\ASPNET;Password=myPassword;Initial Catalog=";\n\ntry\n{\n    // Try to open the connection\n    connection.Open();\n    // If we reach this point, the connection was successful\n    connection.Close();\n}\ncatch (SqlException ex)\n{\n    // Handle the exception\n    Console.WriteLine("Error connecting to SQL Server: " + ex.Message);\n}