using System.IO.Pipes;\nusing System.Security;\n\n// In the parent process\nusing (NamedPipeServerStream pipeServer = new NamedPipeServerStream("SecureStringPipe", PipeDirection.Out))\n{\n    pipeServer.WaitForConnection();\n    using (StreamWriter writer = new StreamWriter(pipeServer))\n    {\n        SecureString secureString = new SecureString();\n        // populate secureString...\n        writer.Write(secureString.ToEncryptedString());\n    }\n}\n\n// In the child process\nusing (NamedPipeClientStream pipeClient = new NamedPipeClientStream(".", "SecureStringPipe", PipeDirection.In))\n{\n    pipeClient.Connect();\n    using (StreamReader reader = new StreamReader(pipeClient))\n    {\n        string encryptedString = reader.ReadToEnd();\n        SecureString secureString = new SecureString();\n        secureString.FromEncryptedString(encryptedString);\n        // use secureString...\n    }\n}