using System;\nusing System.Net;\nusing System.Net.Security;\nusing System.Net.Sockets;\n\nclass TcpSslServer\n{\n    private TcpListener _listener;\n\n    public TcpSslServer(int port)\n    {\n        _listener = new TcpListener(IPAddress.Any, port);\n    }\n\n    public void Start()\n    {\n        _listener.Start();\n        while (true)\n        {\n            TcpClient client = _listener.AcceptTcpClient();\n            NetworkStream stream = client.GetStream();\n            SslStream sslStream = new SslStream(stream, false, ValidateServerCertificate);\n            sslStream.BeginAuthenticateAsServer(GetServerCertificate(), false, AuthenticateAsServerCallback, sslStream);\n        }\n    }\n\n    // ...\n}