protected void GridView1_RowDataBound(object sender, GridViewRowEventArgs e)\n{\n    if (e.Row.RowType == DataControlRowType.DataRow)\n    {\n        Button TimeInButton = (Button)e.Row.FindControl("TimeInButton");\n        Button TimeOutButton = (Button)e.Row.FindControl("TimeOutButton");\n\n        TimeOutButton.OnClientClick = "return toggleTimeInButton(this);";\n        TimeInButton.OnClientClick = "this.style.display='none';";\n    }\n}