using System;\nusing System.Security.Cryptography;\nusing System.Security.Cryptography.X509Certificates;\n\n// Load the certificate from the .CER file\nX509Certificate2 cert = new X509Certificate2(@"c:\temp\whatever.cer");\n\n// Get the public key from the certificate\nRSACryptoServiceProvider rsa = (RSACryptoServiceProvider)cert.PublicKey.Key;\n\n// Generate a random 3DES key\nbyte[] desKey = new byte[24];\nusing (var rng = RandomNumberGenerator.Create())\n{\n    rng.GetBytes(desKey);\n}\n\n// Encrypt the 3DES key using the public key\nbyte[] encryptedKey = rsa.Encrypt(desKey, false);\n\n// Use the encrypted key as the 3DES key\nTripleDESCryptoServiceProvider cryptoProvider = new TripleDESCryptoServiceProvider();\ncryptoProvider.Key = encryptedKey;