using System.Diagnostics;\n\npublic class CpuUsage\n{\n    public static float GetCpuUsage(string processName)\n    {\n        Process[] processes = Process.GetProcessesByName(processName);\n        if (processes.Length > 0)\n        {\n            Process process = processes[0];\n            if (process != null)\n            {\n                return process.TotalProcessorTime.Ticks;\n            }\n        }\n        return 0;\n    }\n\n    public static float GetCpuUsagePercent(string processName)\n    {\n        float cpuUsage = GetCpuUsage(processName);\n        if (cpuUsage > 0)\n        {\n            cpuUsage /= Environment.ProcessorCount;\n            return (cpuUsage / (float)Stopwatch.Frequency) * 100;\n        }\n        return 0;\n    }\n}