using System;\nusing System.ServiceModel;\n\nclass MyServiceHost\n{\n    static void Main(string[] args)\n    {\n        // Create a ServiceHost instance\n        using (ServiceHost host = new ServiceHost(typeof(MyService)))\n        {\n            // Add endpoints and bindings as needed\n            host.AddServiceEndpoint(typeof(IMyService), new NetTcpBinding(), "net.tcp://localhost:8080/MyService");\n\n            // Start the service\n            host.Open();\n\n            Console.WriteLine("Service started. Press <Enter> to stop.");\n            Console.ReadLine();\n\n            // Stop the service\n            host.Close();\n        }\n    }\n}