using Chilkat;\n\nclass EncryptionExample\n{\n    public static string EncryptString(string plainText)\n    {\n        // Create a new Crypt2 object\n        Crypt2 crypt = new Crypt2();\n\n        // Set the encryption key and algorithm\n        crypt.UnlockComponent("your_license_key_here");\n        crypt.put_CryptAlgorithm("aes");\n        crypt.put_KeyLength(256);\n        crypt.put_EncodingMode("base64");\n\n        // Encrypt the string\n        string encrypted = crypt.encryptStringENC(plainText);\n\n        return encrypted;\n    }\n}