using System;\nusing System.Diagnostics;\nusing System.IO;\nusing System.Net.WebSockets;\nusing System.Text;\nusing System.Threading;\nusing System.Threading.Tasks;\n\npublic partial class ConsoleOutputPage : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        // Start the console program as a separate process\n        var process = new Process\n        {\n            StartInfo =\n            {\n                FileName = "console_app.exe",\n                RedirectStandardOutput = true,\n                UseShellExecute = false\n            }\n        };\n        process.Start();\n\n        // Set up the WebSocket connection\n        var socket = new WebSocketServer("ws://localhost:8080/console");\n        socket.OnOpen += (sender, e) =>\n        {\n            // Read the console output and send it to the client\n            var output = process.StandardOutput.ReadToEnd();\n            socket.Send(output);\n        };\n        socket.OnClose += (sender, e) =>\n        {\n            // Clean up\n            process.Kill();\n        };\n        socket.Start();\n    }\n}