using System;\nusing System.IO;\nusing System.Runtime.InteropServices;\n\npublic class MountChecker\n{\n    [DllImport("kernel32.dll", SetLastError = true, CharSet = CharSet.Unicode)]\n    private static extern bool GetFileAttributesW(string lpFileName, out uint lpFileAttributes);\n\n    public static bool IsMountAvailable(string path)\n    {\n        uint attributes;\n        return GetFileAttributesW(path, out attributes) != false;\n    }\n}