using System;\n\npublic class Program\n{\n    private static Random rng = new Random();\n\n    public static void Main()\n    {\n        string[] array = { "apple", "banana", "cherry", "date", "elderberry" }; // your array\n        Shuffle(array);\n    }\n\n    public static void Shuffle<T>(T[] array)\n    {\n        int n = array.Length;\n        while (n > 1) \n        {\n            n--;\n            int k = rng.Next(n + 1);\n            T value = array[k];\n            array[k] = array[n];\n            array[n] = value;\n        }\n    }\n}