using System.Web.SessionState;\n\n// Assuming you have the SessionID in a string variable\nstring sessionId = "your_session_id_here";\n\n// Create a new instance of the SessionStateStoreData class\nSessionStateStoreData sessionData = new SessionStateStoreData(sessionId);\n\n// Get the session data from the State Server\nSessionStateItemCollection sessionItems = sessionData.Items;\n\n// Get the user's identity from the session data\nstring userName = sessionItems["User.Identity.Name"].ToString();