using System;\nusing System.Text.RegularExpressions;\n\npublic bool IsValidOpenIdUri(string openidUri)\n{\n    // Check for null or empty string\n    if (string.IsNullOrEmpty(openidUri))\n    {\n        return false;\n    }\n\n    // Regular expression to validate basic URI syntax\n    var uriRegex = new Regex(@"^https?://[^/]+(?:/[^/]+)*/?$");\n    if (!uriRegex.IsMatch(openidUri))\n    {\n        return false;\n    }\n\n    try\n    {\n        // Attempt to create a Uri object\n        var uri = new Uri(openidUri);\n        // Check for fragment and query parameters\n        if (!string.IsNullOrEmpty(uri.Fragment) || !string.IsNullOrEmpty(uri.Query))\n        {\n            return false;\n        }\n    }\n    catch (UriFormatException)\n    {\n        return false;\n    }\n\n    return true;\n}