using Titanium.Web.Proxy;\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        var proxyServer = new ProxyServer();\n        proxyServer.Start();\n\n        // Set the proxy as the default handler for the browser\n        var browser = new Browser(); // Your browser automation instance\n        browser.SetProxy(proxyServer);\n\n        // Navigate to a website\n        await browser.NavigateAsync("https://example.com");\n\n        // Get the request and response logs\n        var logs = proxyServer.GetLogs();\n        foreach (var log in logs)\n        {\n            Console.WriteLine($"Request: {log.Request.Method} {log.Request.Url}");\n            Console.WriteLine($"Response: {log.Response.StatusCode}");\n        }\n    }\n}