class Foo\n{\n    private static string joke = "yo momma";\n    private static readonly object objectToLockOn = new object();\n\n    private string GetJoke()\n    {\n        lock (objectToLockOn)\n        {\n            return Foo.joke;\n        }\n    }\n\n    private void SetJoke(string newJoke)\n    {\n        lock (objectToLockOn)\n        {\n            Foo.joke = newJoke;\n        }\n    }\n}