// MasterForm.cs\nusing System;\nusing System.Windows.Forms;\n\npublic partial class MasterForm : Form\n{\n    private BindingSource masterBindingSource = new BindingSource();\n\n    public MasterForm()\n    {\n        InitializeComponent();\n\n        // Initialize master data\n        masterBindingSource.DataSource = GetMasterData();\n\n        // Bind master data to DataGridView\n        masterDataGridView.DataSource = masterBindingSource;\n    }\n\n    private void masterDataGridView_CellDoubleClick(object sender, DataGridViewCellEventArgs e)\n    {\n        if (e.RowIndex != -1)\n        {\n            // Get selected item\n            var selectedItem = masterBindingSource[e.RowIndex];\n\n            // Show detail form with selected item\n            var detailForm = new DetailForm(selectedItem);\n            detailForm.ShowDialog();\n        }\n    }\n\n    // Get master data from database or any other source\n    private object[] GetMasterData()\n    {\n        // Replace with actual data\n        return new object[] { "Item 1", "Item 2", "Item 3" };\n    }\n}\n\n// DetailForm.cs\nusing System;\nusing System.Windows.Forms;\n\npublic partial class DetailForm : Form\n{\n    private object selectedItem;\n\n    public DetailForm(object selectedItem)\n    {\n        InitializeComponent();\n\n        this.selectedItem = selectedItem;\n\n        // Bind selected item to detail controls\n        detailTextBox.DataBindings.Add("Text", selectedItem, "Name");\n        // Add more bindings for other controls\n    }\n}