private string GetStringResource(string resourceName)\n{\n    if (string.IsNullOrEmpty(resourceName))\n    {\n        throw new ArgumentException("Resource name cannot be null or empty", nameof(resourceName));\n    }\n\n    try\n    {\n        ResourceManager rm = new ResourceManager("MyApplication.Resources", typeof(MyForm).Assembly);\n        return rm.GetString(resourceName);\n    }\n    catch (MissingManifestResourceException ex)\n    {\n        // Handle missing resource exception\n        Console.WriteLine($"Missing resource: {resourceName}");\n        return string.Empty;\n    }\n    catch (Exception ex)\n    {\n        // Handle other exceptions\n        Console.WriteLine($"Error retrieving resource: {ex.Message}");\n        return string.Empty;\n    }\n}