using System.IdentityModel.Tokens.Jwt;\nusing System.Security.Claims;\n\n// Generate token\nvar tokenHandler = new JwtSecurityTokenHandler();\nvar token = tokenHandler.CreateToken(\n    new SecurityTokenDescriptor\n    {\n        Subject = new ClaimsIdentity(new[] { new Claim("username", "johnDoe") }),\n        Expires = DateTime.UtcNow.AddMinutes(30),\n        SigningCredentials = new SigningCredentials(\n            new SymmetricSecurityKey("your_secret_key_here"),\n            SecurityAlgorithms.HmacSha256)\n    });\n\n// Validate token\nvar tokenValidationParams = new TokenValidationParameters\n{\n    ValidateIssuer = true,\n    ValidateAudience = true,\n    ValidateLifetime = true,\n    ValidateIssuerSigningKey = true,\n    ValidIssuer = "your_issuer_here",\n    ValidAudience = "your_audience_here",\n    IssuerSigningKey = new SymmetricSecurityKey("your_secret_key_here")\n};\n\nvar principal = tokenHandler.ValidateToken(token, tokenValidationParams, out var validatedToken);