[TestClass]\npublic class MyTests\n{\n    private readonly WebProxy _proxy;\n\n    public MyTests()\n    {\n        _proxy = new WebProxy(8080);\n        _proxy.Start();\n    }\n\n    [TestMethod]\n    public async Task TestGetRequest()\n    {\n        var httpClient = new HttpClient();\n        var response = await httpClient.GetAsync("http://localhost:8080/http://example.com");\n        // Verify the response as needed\n    }\n\n    [TestCleanup]\n    public void CleanUp()\n    {\n        _proxy.Dispose();\n    }\n}